import RPi.GPIO as GPIO
import time

pins = [18,17,27,22,23,24,25,5,6,13,12,16]

def led(chunk):
    print(chunk)
    for x in range(0,12):
        if chunk[x]=='1':
            GPIO.output(pins[x],GPIO.HIGH)
    time.sleep(.05)
    for x in range(0,12):
            GPIO.output(pins[x],GPIO.LOW)    
        
def main():
    GPIO.setmode(GPIO.BCM)
    GPIO.setwarnings(False)
    for x in range(0,12):
        GPIO.setup(pins[x],GPIO.OUT)
    str=""
    counter=0;
    print(len(str))
    while counter<len(str)-12:
        chunk=str[counter:12+counter]
        led(chunk)
        counter+=1
    
    
main()
